    private void btnConvertActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnConvertActionPerformed
        // TODO add your handling code here
        /* 
        1. ambil nilai tfcelcius masukan ke variabel celcius
        2. hitung dengan rumus fahrenheit + 1.8 * celcius + 32
        3. ubah tulisan lblfahrentheit dengan isi variabel fahrenheit
        */
        double celcius;
        celcius = Double.parseDouble(tfCelcius.getText());
        double fahrenheit = 1.8 * celcius + 32;
        lblFahrenheit.setText(Double.toString(fahrenheit)+ " fahrenheit");
    }//GEN-LAST:event_btnConvertActionPerformed

